alue['sender_count'];					$data[$key]['uid'] = $value['recipient_uid'];				}				else if ($value['recipient_uid'] == $this->user_id AND $value['recipient_count']) // 当前处于接收用户				{					$data[$key]['user_name'] = $users_info[$value['sender_uid']]['user_name'];					$data[$key]['url_token'] = $users_info[$value['sender_uid']]['url_token'];										$data[$key]['unread'] = $value['recipient_unread'];					$data[$key]['count'] = $value['recipient_count'];										$data[$key]['uid'] = $value['sender_uid'];				}								$data[$key]['last_message'] = $last_message[$value['id']];								$data[$key]['update_time'] = $value['update_time'];								$data[$key]['id'] = $value['id'];			}		}				TPL::assign('list', $data);		TPL::output('m/ajax/inbox_list');	}		public function focus_topics_list_action()	{		if ($topics_list = $this->model('topic')->get_focus_topic_list($this->user_id, intval($_GET['page']) * 5 . ', ' . 5))		{			foreach ($topics_list AS $key => $val)			{				$topics_list[$key]['action_list'] = $this->model('question')->get_questions_list(0, 3, 'new', $val['topic_id']);			}		}				TPL::assign('topics_list', $topics_list);				TPL::output('m/ajax/focus_topics_list');	}}